using (ITokenClient client = new ITokenClient())
{
    TokenClientOptions = new TokenClientOptions()
    {
        ClientId = "myclient",
        ClientSecret = "mysecret"
    };

    TokenResponse response = await client.GetClientCredentialsTokenAsync("https://server.io/connect/token", options, "myscope").ConfigureAwait(false);
    if (!response.IsError)
    {
        string accessToken = response.AccessToken;
    }
}


